<?php
defined('BASEPATH') or exit('No direct script access allowed');

class DataAPIModel extends CI_Model
{
    public function __construct()
    {
        parent::__construct();
        $_ENV['url'] = 'https://balahu.space';
        $_ENV['default_img'] = "";

        $this->load->library('session');
        if(!isset($_SESSION['api_key']))
        {
        $client = new \GuzzleHttp\Client();
        $API_KEY_REQUEST = $client->request('GET', $_ENV['url'] . '/PAW-Rest/api/ApiKey');
        $API_KEY = json_decode($API_KEY_REQUEST->getBody(), true);
        $_SESSION['api_key'] = $API_KEY['api_key'];
        }

    }

}
